import React, { useState } from 'react';
import { getAllStudent, createStudent } from '../services/api'; // Ensure this path is correct

const StudentManagement = () => {
    const [students, setStudents] = useState([]);
    const [studentName, setStudentName] = useState('');
    const [universityName, setUniversityName] = useState('');
    const [universityCourse, setUniversityCourse] = useState('');
    const [showList, setShowList] = useState(false); // To toggle between form and list
    const [error, setError] = useState('');

    // Fetch students when "View" button is clicked
    const fetchStudents = async () => {
        try {
            const response = await getAllStudent();
            setStudents(response.data);
            setShowList(true); // Show the student list
        } catch (error) {
            setError('Failed to fetch students');
        }
    };

    const handleAddStudent = async () => {
        try {
            await createStudent({ studentName, universityName, universityCourse });
            setStudentName('');
            setUniversityName('');
            setUniversityCourse('');
            setError('');
        } catch (error) {
            setError('Failed to add student');
        }
    };

    return (
        <div className="student-management">
            <h2>Student Management</h2>

            {!showList && (
                <div className="add-student">
                    <h3>Add Student</h3>
                    {error && <p className="error">{error}</p>}
                    <input
                        type="text"
                        value={studentName}
                        onChange={(e) => setStudentName(e.target.value)}
                        placeholder="Student Name"
                    />
                    <input
                        type="text"
                        value={universityName}
                        onChange={(e) => setUniversityName(e.target.value)}
                        placeholder="University Name"
                    />
                    <input
                        type="text"
                        value={universityCourse}
                        onChange={(e) => setUniversityCourse(e.target.value)}
                        placeholder="University Course"
                    />
                    <button onClick={handleAddStudent}>Add Student</button>
                    <button onClick={fetchStudents}>View Students</button>
                </div>
            )}
        </div>
    );
};

export default StudentManagement;
