import axios from 'axios';

// Configure Axios instance
const API = axios.create({
    baseURL: 'http://localhost:8090', // Spring Boot backend URL
});

// Register user
export const registerUser = async (userData) => {
    return API.post('/auth/register', userData);
};

// Login user
export const loginUser = async (loginData) => {
    return API.post('/auth/login', loginData);
};

// Create application
export const createApplication = async (applicationData, studentId) => {
    return API.post(`/applications?studentId=${studentId}`, applicationData);
};

// Get all applications
export const getApplications = async (studentId) => {
    return API.get(`/applications?studentId=${studentId}`);
};
