import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { loginUser } from '../services/api';
import './Login.css';

const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [showNotification, setShowNotification] = useState(false); // State for notification
    const [notificationMsg, setNotificationMsg] = useState(''); // State for notification message
    const navigate = useNavigate();

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            await loginUser(email, password);
            setNotificationMsg('Login successful! Redirecting to dashboard...');
            setShowNotification(true); // Show success notification
            setTimeout(() => {
                setShowNotification(false); // Hide notification after 3 seconds
                navigate('/dashboard'); // Redirect to dashboard
            }, 3000);
        } catch (err) {
            setNotificationMsg('Invalid email or password');
            setShowNotification(true); // Show error notification
            setError('Invalid email or password');
            setTimeout(() => {
                setShowNotification(false); // Hide notification after 3 seconds
            }, 3000);
        }
    };

    return (
        <div className="login-container">
            <div className="login-form">
                <h2>Login</h2>
                {error && <p className="error">{error}</p>}
                <form onSubmit={handleLogin}>
                    <div className="form-group">
                        <label htmlFor="email">Email</label>
                        <input
                            type="email"
                            id="email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            required
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="password">Password</label>
                        <input
                            type="password"
                            id="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                        />
                    </div>
                    <button type="submit">Login</button>
                </form>
                <p>Don't have an account? <a href="/register">Sign Up</a></p>
            </div>
            {showNotification && (
                <div className="notification">
                    {notificationMsg}
                </div>
            )}
        </div>
    );
};

export default Login;
