import React, { useState, useEffect } from 'react';
import { createApplication, getApplications } from '../services/api';

const Dashboard = () => {
    const [applicationData, setApplicationData] = useState({
        universityName: '',
        universityCourse: '',
    });
    const [applications, setApplications] = useState([]);
    const studentId = localStorage.getItem("studentId");

    const handleChange = (e) => {
        setApplicationData({ ...applicationData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await createApplication(applicationData, studentId);
            fetchApplications();
            alert("Application created successfully");
        } catch (error) {
            console.error("Error creating application", error);
            alert("Failed to create application");
        }
    };

    const fetchApplications = async () => {
        try {
            const response = await getApplications(studentId);
            setApplications(response.data);
        } catch (error) {
            console.error("Error fetching applications", error);
        }
    };

    useEffect(() => {
        fetchApplications();
    }, []);

    return (
        <div className="dashboard-container">
            <h2>Dashboard</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>University Name:</label>
                    <input type="text" name="universityName" value={applicationData.universityName} onChange={handleChange} />
                </div>
                <div>
                    <label>University Course:</label>
                    <input type="text" name="universityCourse" value={applicationData.universityCourse} onChange={handleChange} />
                </div>
                <button type="submit">Create Application</button>
            </form>
            
            <h3>Your Applications</h3>
            <ul>
                {applications.map((application) => (
                    <li key={application.id}>
                        {application.universityName} - {application.universityCourse}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Dashboard;
