import React, { useEffect, useState } from 'react';
import { getStudents } from '../services/api'; // Ensure this path is correct
import { useNavigate } from 'react-router-dom'; // For routing to other pages

const ViewStudents = () => {
    const [students, setStudents] = useState([]);
    const [error, setError] = useState('');
    const navigate = useNavigate(); // For handling navigation

    useEffect(() => {
        // Fetch students when the component mounts
        const fetchStudents = async () => {
            try {
                const data = await getStudents();
                setStudents(data);
            } catch (error) {
                setError('Failed to fetch students');
            }
        };

        fetchStudents();
    }, []);

    const handleLogout = () => {
        // Perform any necessary logout logic here (e.g., clearing tokens or session)
        navigate('/login'); // Navigate to the login page
    };

    return (
        <div className="view-students">
            <h3>Student List</h3>
            {error && <p className="error">{error}</p>}
            {students.length > 0 ? (
                <ul>
                    {students.map((student) => (
                        <li key={student.id}>
                            {student.name} - {student.university} - {student.course}
                        </li>
                    ))}
                </ul>
            ) : (
                <p>No students available.</p>
            )}
            <button onClick={handleLogout}>Logout</button>
        </div>
    );
};

export default ViewStudents;
