import axios from 'axios';

const API_URL = 'http://localhost:8090/api';

export const registerUser = async (user) => {
    try {
        const response = await axios.post(`${API_URL}/users/register`, user);
        return response.data; // Return the user data if registration is successful
    } catch (err) {
        if (err.response && err.response.status === 409) {
            // If the email is already in use (HTTP 409 Conflict)
            return null; // Return null to indicate the email already exists
        }
        throw err; // For other errors, throw the error
    }
};


export const loginUser = (email, password) => {
    return axios.post(`${API_URL}/users/login`, null, { params: { email, password } });
};

export const createStudent = (student) => {
    return axios.post(`${API_URL}/students`, student);
};

export const getAllStudent = () => {
    return axios.get(`${API_URL}/students`);
};
