import React, { useState, useEffect } from 'react';
import { createApplication, getApplications } from '../services/api';
import './Dashboard.css'; // Import the CSS file

function Dashboard() {
    const [applications, setApplications] = useState([]);
    const [studentName, setStudentName] = useState('');
    const [universityName, setUniversityName] = useState('');
    const [universityCourse, setUniversityCourse] = useState('');

    useEffect(() => {
        async function fetchApplications() {
            try {
                const response = await getApplications();
                setApplications(response.data);
            } catch (error) {
                console.error('Failed to fetch applications', error);
            }
        }

        fetchApplications();
    }, []);

    const handleCreateApplication = async () => {
        try {
            await createApplication({ studentName, universityName, universityCourse });
            // Optionally refetch applications or update state
            const response = await getApplications(); // Refresh the list
            setApplications(response.data);
        } catch (error) {
            console.error('Failed to create application', error);
        }
    };

    return (
        <div className="dashboard-container">
            <h2>Dashboard</h2>
            <h3>Create Application</h3>
            <input 
                type="text" 
                value={studentName} 
                onChange={(e) => setStudentName(e.target.value)} 
                placeholder="Student Name" 
            />
            <input 
                type="text" 
                value={universityName} 
                onChange={(e) => setUniversityName(e.target.value)} 
                placeholder="University Name" 
            />
            <input 
                type="text" 
                value={universityCourse} 
                onChange={(e) => setUniversityCourse(e.target.value)} 
                placeholder="University Course" 
            />
            <button onClick={handleCreateApplication}>Create Application</button>
            <h3>Applications</h3>
            <ul>
                {applications.map(app => (
                    <li key={app._id}>
                        {app.studentName} - {app.universityName} - {app.universityCourse}
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default Dashboard;
