import React, { useState } from 'react';
import { createStudent } from '../services/api'; // Ensure this path is correct
import { useNavigate } from 'react-router-dom'; // For routing to login and view students

const AddStudent = () => {
    const [studentName, setStudentName] = useState('');
    const [universityName, setUniversityName] = useState('');
    const [universityCourse, setUniversityCourse] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate(); // For handling logout and routing

    const handleAddStudent = async () => {
        try {
            await createStudent({ studentName, universityName, universityCourse });
            setStudentName('');
            setUniversityName('');
            setUniversityCourse('');
            setError('');
        } catch (error) {
            setError('Failed to add student');
        }
    };

    const handleView = () => {
        navigate('/view-students'); // Navigate to the view students page
    };

    const handleLogout = () => {
        // Perform any necessary logout logic here (e.g., clearing tokens or session)
        navigate('/login'); // Navigate to the login page
    };

    return (
        <div className="add-student">
            <h3>Add Student</h3>
            {error && <p className="error">{error}</p>}
            <input
                type="text"
                value={studentName}
                onChange={(e) => setStudentName(e.target.value)}
                placeholder="Student Name"
            />
            <input
                type="text"
                value={universityName}
                onChange={(e) => setUniversityName(e.target.value)}
                placeholder="University Name"
            />
            <input
                type="text"
                value={universityCourse}
                onChange={(e) => setUniversityCourse(e.target.value)}
                placeholder="University Course"
            />
            <button onClick={handleAddStudent}>Add Student</button>
            <button onClick={handleView}>View Students</button>
            <button onClick={handleLogout}>Logout</button>
        </div>
    );
};

export default AddStudent;
