import React, { useState } from 'react';
import { createStudent } from '../services/api'; // Assuming this API function exists
import './AddStudent.css';

const AddStudent = () => {
    const [studentName, setStudentName] = useState('');
    const [universityName, setUniversityName] = useState('');
    const [universityCourse, setUniversityCourse] = useState('');
    const [error, setError] = useState('');
    const [notification, setNotification] = useState(''); // State for notification

    const handleAddStudent = async () => {
        try {
            await createStudent({ studentName, universityName, universityCourse });
            setNotification('Student added successfully!');
            setStudentName('');
            setUniversityName('');
            setUniversityCourse('');
            setError('');
            setTimeout(() => {
                setNotification(''); // Clear notification after 3 seconds
            }, 3000);
        } catch (err) {
            setError('Failed to add student');
            setNotification('Failed to add student');
            setTimeout(() => {
                setNotification(''); // Clear notification after 3 seconds
            }, 3000);
        }
    };

    return (
        <div className="add-student-container">
            <div className="add-student-form">
                <h2>Add Student</h2>
                {error && <p className="error">{error}</p>}
                <input
                    type="text"
                    value={studentName}
                    onChange={(e) => setStudentName(e.target.value)}
                    placeholder="Student Name"
                    required
                />
                <input
                    type="text"
                    value={universityName}
                    onChange={(e) => setUniversityName(e.target.value)}
                    placeholder="University Name"
                    required
                />
                <input
                    type="text"
                    value={universityCourse}
                    onChange={(e) => setUniversityCourse(e.target.value)}
                    placeholder="University Course"
                    required
                />
                <button onClick={handleAddStudent}>Add Student</button>
            </div>
            {notification && (
                <div className="notification">
                    {notification}
                </div>
            )}
        </div>
    );
};

export default AddStudent;
